    choiceableMasteryTypes: [MasteryType.strumentiArtigiano, MasteryType.strumentiMusicali],
    choiceableSubSkillsMultiClass: false,
    savingThrows: [Skill.destrezza, Skill.forza],
    choiceableItems: [
      {
        'spadaCorta': 1,
